-- ----------------------------
-- Table structure for product
-- ----------------------------
DROP TABLE IF EXISTS `product`;
CREATE TABLE `product` (
  `product_id` mediumint unsigned NOT NULL AUTO_INCREMENT COMMENT '商品id ',
  `product_name` varchar(120) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '商品的名称 ',
  `product_sn` varchar(60) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '商品的唯一货号 ',
  `product_tsn` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_estonian_ci DEFAULT NULL COMMENT '条形编码',
  `product_stock` smallint unsigned NOT NULL DEFAULT '0' COMMENT '商品库存数量 ',
  `product_price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '本店售价 ',
  `market_price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '市场售价 ',
  `shipping_tpl_id` mediumint unsigned NOT NULL DEFAULT '0' COMMENT '运费模板id',
  `product_status` tinyint unsigned NOT NULL DEFAULT '1' COMMENT '该商品状态，1，上架；0，下架',
  `product_type` tinyint(1) DEFAULT '1' COMMENT '商品类型 1普通商品 2虚拟商品3 卡密商品 4 付费内容',
  `category_id` smallint unsigned NOT NULL DEFAULT '0' COMMENT '商品所属商品分类id，取值category的cat_id ',
  `brand_id` smallint unsigned NOT NULL DEFAULT '0' COMMENT '品牌id，取值于brand 的brand_id ',
  `shop_id` int unsigned NOT NULL DEFAULT '0' COMMENT '店铺id',
  `keywords` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '商品关键字，放在商品页的关键字中，为搜索引擎收录用 ',
  `shop_category_id` int unsigned NOT NULL DEFAULT '0' COMMENT '店铺分类id',
  `check_status` tinyint unsigned NOT NULL DEFAULT '1' COMMENT '审核状态:0,待审核;1,审核通过;2,审核未通过',
  `check_reason` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '审核失败原因',
  `click_count` int unsigned NOT NULL DEFAULT '0' COMMENT '商品点击数',
  `product_weight` decimal(10,3) unsigned NOT NULL DEFAULT '0.000' COMMENT '商品的重量，以千克为单位 ',
  `is_promote` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '是否特价促销；0，否；1，是 ',
  `is_promote_activity` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否参加了秒杀',
  `promote_price` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '促销价格 ',
  `promote_start_date` int unsigned NOT NULL DEFAULT '0' COMMENT '促销价格开始日期 ',
  `promote_end_date` int unsigned NOT NULL DEFAULT '0' COMMENT '促销价格结束日期 ',
  `seckill_max_num` smallint unsigned NOT NULL DEFAULT '0' COMMENT '最大秒杀库存',
  `product_brief` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '商品的简短描述 ',
  `product_desc` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci COMMENT '商品的详细描述 ',
  `pic_url` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '商品的实际大小图片，如进入该商品页时介绍商品属性所显示的大图片',
  `pic_thumb` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '商品在前台显示的微缩图片，如在分类筛选时显示的小图片 ',
  `pic_original` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '应该是上传的商品的原始图片 ',
  `comment_tag` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT NULL COMMENT '标签',
  `free_shipping` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '是否包邮',
  `integral` int NOT NULL DEFAULT '0' COMMENT '购买该商品可以使用的积分数量，估计应该是用积分代替金额消费；但程序好像还没有实现该功能 ',
  `add_time` int unsigned NOT NULL DEFAULT '0' COMMENT '商品的添加时间 ',
  `sort_order` smallint unsigned NOT NULL DEFAULT '100' COMMENT '商品的显示顺序',
  `store_sort_order` smallint unsigned NOT NULL DEFAULT '100' COMMENT '店铺的排序',
  `is_delete` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '商品是否已经删除，0，否；1，已删除 ',
  `is_best` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '是否是精品:0，否；1，是 ',
  `is_new` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '是否是新品:0，否；1，是',
  `is_hot` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '是否热销:0，否；1，是 ',
  `last_update` int unsigned NOT NULL DEFAULT '0' COMMENT '最近一次更新商品配置的时间 ',
  `remark` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '' COMMENT '商品的商家备注，仅商家可见 ',
  `give_integral` int NOT NULL DEFAULT '-1' COMMENT '购买该商品时每笔成功交易赠送的积分数量 ',
  `rank_integral` int NOT NULL DEFAULT '-1' COMMENT '等级积分',
  `suppliers_id` smallint unsigned DEFAULT NULL COMMENT '供应商id',
  `virtual_sales` int unsigned NOT NULL DEFAULT '0' COMMENT '虚拟销量',
  `limit_number` int unsigned NOT NULL DEFAULT '0' COMMENT '限购数量',
  `product_care` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci COMMENT '温馨提示',
  `product_related` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci COMMENT '[JSON]关联的推荐商品Id',
  `product_service_ids` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci COMMENT '[JSON]服务项ids',
  `is_support_return` tinyint unsigned NOT NULL DEFAULT '0' COMMENT '是否支持无理由退货',
  `is_support_cod` tinyint unsigned NOT NULL DEFAULT '1' COMMENT '是否支持货到付款:0,否;1,是',
  `product_video` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci COMMENT '商品视频',
  `prepay_price` decimal(8,2) unsigned DEFAULT '0.00' COMMENT '预付金额',
  `card_group_id` int NOT NULL DEFAULT '0' COMMENT '关联的电子卡券分组',
  `virtual_sample` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci COMMENT '虚拟商品',
  `paid_content` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci COMMENT '付费内容',
  `no_shipping` tinyint DEFAULT '0' COMMENT '是否无需配送0不是1是',
  `fixed_shipping_type` tinyint DEFAULT '2' COMMENT '运费类型1固定运费2运费模板',
  `fixed_shipping_fee` decimal(10,2) DEFAULT '0.00' COMMENT '固定运费金额',
  `vendor_product_id` int DEFAULT NULL COMMENT '供应商产品表id',
  `vendor_id` int DEFAULT NULL COMMENT '供应商id',
  PRIMARY KEY (`product_id`,`brand_id`,`product_weight`) USING BTREE,
  KEY `goods_sn` (`product_sn`) USING BTREE,
  KEY `cat_id` (`category_id`) USING BTREE,
  KEY `last_update` (`last_update`) USING BTREE,
  KEY `brand_id` (`brand_id`) USING BTREE,
  KEY `goods_weight` (`product_weight`) USING BTREE,
  KEY `promote_end_date` (`promote_end_date`) USING BTREE,
  KEY `promote_start_date` (`promote_start_date`) USING BTREE,
  KEY `goods_number` (`product_stock`) USING BTREE,
  KEY `sort_order` (`sort_order`) USING BTREE,
  KEY `goods_basesell` (`virtual_sales`) USING BTREE,
  KEY `store_id` (`shop_id`) USING BTREE,
  KEY `product_name` (`product_name`) USING BTREE,
  KEY `keywords` (`keywords`) USING BTREE,
  KEY `idx_vendor_product_id` (`vendor_product_id`),
  KEY `idx_vendor_id` (`vendor_id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb3 ROW_FORMAT=DYNAMIC COMMENT='商品表';

